// Парсинг данных из webhook запроса
// Подготовка данных для VTB API

console.log('=== VTB SBP Payment Handler ===');
console.log('Input:', JSON.stringify($input, null, 2));

// Получаем данные из body
let requestData = null;

if ($input.item && $input.item.json && $input.item.json.body) {
  const body = $input.item.json.body;
  requestData = typeof body === 'string' ? JSON.parse(body) : body;
  console.log('✅ Данные найдены в: $input.item.json.body');
} else if ($input.item && $input.item.json) {
  requestData = $input.item.json;
  console.log('✅ Данные найдены в: $input.item.json');
} else if ($input.body) {
  requestData = typeof $input.body === 'string' ? JSON.parse($input.body) : $input.body;
  console.log('✅ Данные найдены в: $input.body');
} else {
  console.error('❌ Данные не найдены!');
  return {
    json: {
      error: true,
      message: '❌ Ошибка: данные не найдены в запросе',
      errorCode: 'MISSING_DATA'
    }
  };
}

console.log('requestData:', JSON.stringify(requestData, null, 2));

// Извлекаем обязательные параметры
const amount = requestData.amount || requestData.totalAmount || 0;
const orderNumber = requestData.orderNumber || `ORDER-${Date.now()}`;
const description = requestData.description || requestData.orderDescription || 'Оплата заказа';
const returnUrl = requestData.returnUrl || 'https://zakazhi.org/payment/return';
const testMode = requestData.testMode !== false; // по умолчанию тестовый режим

// Валидация
if (!amount || amount <= 0) {
  return {
    json: {
      error: true,
      message: '❌ Ошибка: не указана сумма платежа',
      errorCode: 'INVALID_AMOUNT'
    }
  };
}

// Конвертируем сумму в копейки, если она в рублях
const amountInKopecks = amount < 100 ? amount * 100 : amount;

// Данные для VTB API (из переменных окружения или настроек)
// ВАЖНО: Эти данные должны быть в переменных окружения n8n!
const vtbBaseUrl = $env.VTB_BASE_URL || 'https://vtb.rbsuat.com/payment/rest/';
const vtbUserName = $env.VTB_USERNAME || requestData.vtbUserName || requestData.username || 'Restotest125';
const vtbPassword = $env.VTB_PASSWORD || 'mmnkh-B5';

console.log('Amount (kopecks):', amountInKopecks);
console.log('Order Number:', orderNumber);
console.log('Description:', description);
console.log('Test Mode:', testMode);

// Возвращаем подготовленные данные
return {
  json: {
    // Данные запроса
    amount: amountInKopecks,
    orderNumber: orderNumber,
    description: description,
    returnUrl: returnUrl,
    testMode: testMode,

    // Данные для VTB API
    vtbBaseUrl: vtbBaseUrl,
    vtbUserName: vtbUserName,
    vtbPassword: vtbPassword,

    // Оригинальные данные
    originalData: requestData
  }
};

// Проверка результата регистрации заказа
// Подготовка данных для создания QR-кода

console.log('=== Register Order Response ===');
console.log('Response:', JSON.stringify($input, null, 2));

// Получаем ответ от VTB
let registerResponse = null;

if ($input.item && $input.item.json) {
  registerResponse = $input.item.json;
} else if ($input.json) {
  registerResponse = $input.json;
} else {
  registerResponse = $input;
}

console.log('Register Response:', JSON.stringify(registerResponse, null, 2));

// Проверяем наличие ошибки
if (registerResponse.errorCode) {
  return {
    json: {
      error: true,
      message: `❌ Ошибка регистрации заказа: ${registerResponse.errorMessage || registerResponse.errorCode}`,
      errorCode: registerResponse.errorCode,
      errorMessage: registerResponse.errorMessage
    }
  };
}

// Проверяем наличие orderId
if (!registerResponse.orderId) {
  return {
    json: {
      error: true,
      message: '❌ Ошибка: не получен orderId от VTB',
      errorCode: 'NO_ORDER_ID'
    }
  };
}

// Получаем данные из предыдущего узла
const previousData = $('Parse Request Data').item.json;

console.log('Order ID:', registerResponse.orderId);
console.log('Previous data:', JSON.stringify(previousData, null, 2));

// Возвращаем данные для создания QR-кода
return {
  json: {
    // Данные из регистрации
    orderId: registerResponse.orderId,
    formUrl: registerResponse.formUrl,

    // Данные из предыдущего узла
    amount: previousData.amount,
    orderNumber: previousData.orderNumber,
    description: previousData.description,
    testMode: previousData.testMode,

    // Данные для VTB API
    vtbBaseUrl: previousData.vtbBaseUrl,
    vtbUserName: previousData.vtbUserName,
    vtbPassword: previousData.vtbPassword
  }
};

// Обработка результата создания QR-кода
// Форматирование ответа для клиента

console.log('=== Create QR Response ===');
console.log('Response:', JSON.stringify($input, null, 2));

// Получаем ответ от VTB
let qrResponse = null;

if ($input.item && $input.item.json) {
  qrResponse = $input.item.json;
} else if ($input.json) {
  qrResponse = $input.json;
} else {
  qrResponse = $input;
}

console.log('QR Response:', JSON.stringify(qrResponse, null, 2));

// Проверяем наличие ошибки
if (qrResponse.errorCode) {
  return {
    json: {
      error: true,
      message: `❌ Ошибка создания QR-кода: ${qrResponse.errorMessage || qrResponse.errorCode}`,
      errorCode: qrResponse.errorCode,
      errorMessage: qrResponse.errorMessage
    }
  };
}

// Проверяем наличие QR-кода
if (!qrResponse.qrId || !qrResponse.qrImage) {
  return {
    json: {
      error: true,
      message: '❌ Ошибка: не получен QR-код от VTB',
      errorCode: 'NO_QR_CODE'
    }
  };
}

// Получаем данные из предыдущих узлов
const registerData = $('Check Register Response').item.json;

console.log('QR ID:', qrResponse.qrId);
console.log('QR Image (length):', qrResponse.qrImage ? qrResponse.qrImage.length : 0);

// Формируем ответ для клиента
const response = {
  success: true,
  message: 'QR-код успешно создан',

  // Данные платежа
  orderId: registerData.orderId,
  orderNumber: registerData.orderNumber,
  amount: registerData.amount,
  amountRubles: (registerData.amount / 100).toFixed(2),
  description: registerData.description,

  // Данные QR-кода
  qrId: qrResponse.qrId,
  qrUrl: qrResponse.qrUrl || '',
  qrImage: qrResponse.qrImage, // Base64 изображение
  qrImageUrl: qrResponse.qrImage ? `data:image/png;base64,${qrResponse.qrImage}` : null,

  // Тестовый режим
  testMode: registerData.testMode,

  // Метаданные
  timestamp: new Date().toISOString(),
  expiresAt: new Date(Date.now() + 30 * 60 * 1000).toISOString() // QR-код действителен 30 минут
};

console.log('Final Response:', JSON.stringify(response, null, 2));

return {
  json: response
};
