// Парсинг данных для симуляции платежа
// В тестовом режиме симулируем успешную оплату

console.log('=== VTB SBP Test Payment Simulator ===');
console.log('Input:', JSON.stringify($input, null, 2));

// Получаем данные из body
let requestData = null;

if ($input.item && $input.item.json && $input.item.json.body) {
  const body = $input.item.json.body;
  requestData = typeof body === 'string' ? JSON.parse(body) : body;
} else if ($input.item && $input.item.json) {
  requestData = $input.item.json;
} else if ($input.body) {
  requestData = typeof $input.body === 'string' ? JSON.parse($input.body) : $input.body;
}

if (!requestData) {
  return {
    json: {
      error: true,
      message: '❌ Ошибка: данные не найдены в запросе',
      errorCode: 'MISSING_DATA'
    }
  };
}

// Извлекаем параметры
const orderId = requestData.orderId;
const qrId = requestData.qrId;
const status = requestData.status || 'PAID'; // PAID, REJECTED_BY_USER, EXPIRED
const amount = requestData.amount || 0;
const orderNumber = requestData.orderNumber || 'UNKNOWN';

if (!orderId || !qrId) {
  return {
    json: {
      error: true,
      message: '❌ Ошибка: не указаны orderId или qrId',
      errorCode: 'MISSING_IDS'
    }
  };
}

console.log('Simulating payment:');
console.log('Order ID:', orderId);
console.log('QR ID:', qrId);
console.log('Status:', status);

// Симулируем webhook от VTB
const webhookPayload = {
  orderId: orderId,
  qrId: qrId,
  status: status,
  amount: amount,
  currency: 643,
  orderNumber: orderNumber,
  timestamp: new Date().toISOString()
};

return {
  json: {
    success: true,
    message: `Платеж успешно симулирован со статусом: ${status}`,
    webhookPayload: webhookPayload,

    // Данные для отправки на webhook обработки платежей
    orderId: orderId,
    qrId: qrId,
    status: status,
    amount: amount,
    orderNumber: orderNumber
  }
};

// Форматирование ответа после симуляции

const simulationData = $('Prepare Simulation').item.json;
const webhookResponse = $input.item ? $input.item.json : $input.json;

return {
  json: {
    success: true,
    message: 'Платеж успешно симулирован и отправлен на обработку',

    // Данные симуляции
    orderId: simulationData.orderId,
    qrId: simulationData.qrId,
    status: simulationData.status,
    amount: simulationData.amount,
    orderNumber: simulationData.orderNumber,

    // Ответ от webhook обработчика
    webhookResponse: webhookResponse,

    timestamp: new Date().toISOString()
  }
};
